mapper.select:
SELECT NVL(SUM(price * amount), 0) money
FROM CART c, PRODUCT p
WHERE c.productNo = p.productNo AND c.memberId = #{memberId}

SELECT COUNT(*)
FROM CART
WHERE memberId = #{memberId}
AND productNo = #{productNo}

mapper.update:
UPDATE CART
SET amount = amount + #{amount}
WHERE memberId = #{memberId}
AND productNo = #{productNo}

UPDATE CART
SET amount = #{amount}
WHERE memberId = #{memberId}
AND productNo = #{productNo}
